import numpy as npfrom data.data_generator import data_generatordef sanity_checks(x, theta, t_max=100, dt=1, to_tensor=True, threshold_S=0.25):    """    Implement sanity checks    :param X:    :param theta:    :param t:    :param initial_values:    :return:    """    N = x.shape[0]    num_instances = 0    while num_instances != N:        if N - num_instances != N:            extra_data = data_generator(batch_size=(N - num_instances), t_max=t_max, dt=dt, N=N, to_tensor=to_tensor)            extra_X = np.array(extra_data['x'])            extra_theta = np.array(extra_data['theta'])            x = np.concatenate((x, extra_X))            theta = np.concatenate((theta, extra_theta))        idx_S = np.argwhere(x[:, -1, 0] > threshold_S)        idx = np.unique(idx_S)        x = np.delete(x, idx, axis=0)        theta = np.delete(theta, idx, axis=0)        num_instances = x.shape[0]    # x = x.reshape(x.shape[0], -1)    return x, theta